controllersModule.controller('AllGroupsCtrl', function($scope, $location, GroupSrvc, StudentSrvc){
    $scope.page = {};

    // Инициализация данных
    $scope.page.init = function(){
        /* Данные для таблицы с группами, объект будет передан в компонент dmgrid
           Заголовок таблицы. 
           Сss класс для таблицы. 
           Имена колонок и имя свойства для доступа к значению.
           Методы для работы с данными */
        $scope.page.grgrid = {
            caption: 'Группы',
            cssClass: 'table table-bordered table-hover',
            columns: [{name: 'Группа', property: 'name'}, 
                      {name: 'Факультет', property: 'faculty.name'},
                      {name: 'Количество студентов', property: 'students.length'}],
            add: $scope.page.addGroup,
            edit: $scope.page.editGroup,
            remove: $scope.page.removeGroup
        };

        // Данные для таблицы со студентами, объект будет передан в компонент dmgrid
        $scope.page.stgrid = {
            caption: 'Студенты',
            cssClass: 'table table-bordered table-hover',
            columns: [{name: 'Фамилия', property: 'lname'}, 
                      {name: 'Имя', property: 'fname'},
                      {name: 'Email', property: 'email'},
                      {name: 'Адрес', property: 'address'},
                      {name: 'Группа', property: 'group.name'},
                      {name: 'Факультет', property: 'group.faculty.name'}],
            add: $scope.page.addStudent,
            edit: $scope.page.editStudent,
            remove: $scope.page.removeStudent
        };

        // Загрузить все группы из БД
        $scope.page.loadGroups();
   };


    // Загрузить все группы из БД
    $scope.page.loadGroups = function(){
        GroupSrvc.getAll().then(
            function(data){
                // Успех. Передать данные в грид
                $scope.page.grgrid.items = data.children;
            },
            function(data, status, headers, config){
                // Ошибка
                $scope.page.gralert = {title: 'Внимание!', msg: 'Группы не загружены. ' + data, cssClass: 'alert alert-error', visible: true};  
            });      
    };

    // Загрузить студентов группы
    $scope.page.loadGroupStudents = function(group){
        GroupSrvc.getStudents(group.id).then(
            function(data){
                group.students = data.children;
            },
            function(data, status, headers, config){
                $scope.page.gralert = {title: 'Внимание!', msg: 'Студенты группы не загружены. ' + data, cssClass: 'alert alert-error', visible: true};  
            });       
    };

    // Добавить группу. Переход на другую страницу
    $scope.page.addGroup = function(){
        $location.path('/group/');
    };

    // Редактировать группу. Переход на другую страницу
    $scope.page.editGroup = function(group){
        $location.path('/group/' + group.id);
    };

    // Удалить группу
    $scope.page.removeGroup = function(group){
        GroupSrvc.remove(group.id).then(
            function(data){
                $scope.page.gralert = {title: 'Готово!', msg: 'Группа удалена.', cssClass: 'alert alert-success', visible: true, closeTimeout: 1500};
                $scope.page.loadGroups();
                $scope.page.grgrid.selected = null;
            },
            function(data, status, headers, config){
                $scope.page.gralert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
            });
    };

    // Добавить студента в группу, которая выбрана в таблице. Переход на другую страницу
    $scope.page.addStudent = function(){
        $location.path('/student/faculty/' + $scope.page.grgrid.selected.faculty.id + '/group/' + $scope.page.grgrid.selected.id);
    };

    // Редактировать студента. Переход на другую страницу
    $scope.page.editStudent = function(student){
        $location.path('/student/' + student.id);
    };

    // Удалить студента
    $scope.page.removeStudent = function(student){
        StudentSrvc.remove(student.id).then(
            function(data){
                $scope.page.stalert = {title: 'Готово!', msg: 'Студент удален.', cssClass: 'alert alert-success', visible: true, closeTimeout: 1500};
                $scope.page.loadGroupStudents($scope.page.grgrid.selected);
                $scope.page.stgrid.selected = null;
            },
            function(data, status, headers, config){
                $scope.page.stalert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
            });
    };

    // Отслеживание выбранной группы, если изменилась, то заменить студентво в таблице
    $scope.$watch('page.grgrid.selected', function(){
        if ($scope.page.grgrid.selected)
      	   $scope.page.stgrid.items = $scope.page.grgrid.selected.students;  
    },true);

    $scope.page.init(); 
});

'use strict';
