controllersModule.controller('AllStudentsCtrl', function($scope, $location, StudentSrvc){
   $scope.page = {};

   // Инициализация данных
   $scope.page.init = function(){
     /* Данные для таблицы с группами, объект будет передан в компонент dmgrid
        Заголовок таблицы. 
        Сss класс для таблицы. 
        Имена колонок и имя свойства для доступа к значению.
        Методы для работы с данными */
        $scope.page.grid = {
            caption: 'Студенты',
            cssClass: 'table table-bordered table-hover',
            columns: [{name: 'Фамилия', property: 'lname'}, 
                      {name: 'Имя', property: 'fname'},
                      {name: 'Email', property: 'email'},
                      {name: 'Адрес', property: 'address'},
                      {name: 'Группа', property: 'group.name'},
                      {name: 'Факультет', property: 'group.faculty.name'}],
            add: $scope.page.addStudent,
            edit: $scope.page.editStudent,
            remove: $scope.page.removeStudent
        };

        $scope.page.loadStudents();
   };

   // Загрузить всех студентов
   $scope.page.loadStudents = function(){
        StudentSrvc.getAll().then(
            function(data){
                $scope.page.grid.items = data.children;
            },
            function(data, status, headers, config){
                $scope.page.gralert = {title: 'Внимание!', msg: 'Студенты не загружены. ' + data, cssClass: 'alert alert-error', visible: true};  
            });      
   };

   // Перейти на др. страницу для добавления студента
   $scope.page.addStudent = function(){
        $location.path('/student/');
   };

   // Перейти на др. страницу для редактирования студента по ИД
   $scope.page.editStudent = function(student){
        $location.path('/student/' + student.id);
   };

   // Удалить студента по ИД
   $scope.page.removeStudent = function(student){
        StudentSrvc.remove(student.id).then(
            function(data){
              	$scope.page.alert = {title: 'Готово!', msg: 'Студент удален.', cssClass: 'alert alert-success', visible: true, closeTimeout: 1500};
            	  $scope.page.loadStudents();
            	  $scope.page.grid.selected = null;
            },
            function(data, status, headers, config){
                $scope.page.alert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
            });
   };

   $scope.page.init(); 
});

