/*===========================================================================================
Группа. Изменение/Добавление
===========================================================================================*/

controllersModule.controller('GroupCtrl', function($scope, $routeParams, GroupSrvc, FacultySrvc){
   $scope.page = {group:{}};

   // Инициализация данных
   $scope.page.init = function(){
      if ($routeParams.id){
          $scope.page.formCaption = "Редактирование группы";
          $scope.page.formBtnSubmitName = "Сохранить";
          $scope.page.loadGroup($routeParams.id);        
      }
      else{
          $scope.page.formCaption = "Добавление группы";
          $scope.page.formBtnSubmitName = "Добавить";
      }

      $scope.page.loadFaculties();
   };

   // Загрузить группу по ИД
   $scope.page.loadGroup = function(id){
      GroupSrvc.get(id).then(
          function(data){
              $scope.page.group = data;
          },
          function(data, status, headers, config){
              $scope.page.gralert = {title: 'Внимание!', msg: 'Группы не загружены. ' + data, cssClass: 'alert alert-error', visible: true};  
          });  
   };

  // Загрузить все факультеты
  $scope.page.loadFaculties = function(){
      FacultySrvc.getAll(false).then(
          function(data){
              $scope.page.faculties = data.children;
          },
          function(data, status, headers, config){
              $scope.page.gralert = {title: 'Внимание!', msg: 'Факультеты не загружены. ' + data, cssClass: 'alert alert-error', visible: true};  
          });      
   };

   // Сохранить/создать (отправка формы). Передаем объект на сервер
   $scope.page.submit = function(){
      GroupSrvc.save($scope.page.group).then(
          function(data){
              var msg = 'Изменения сохранены.';

              if (!$routeParams.id){
                  msg = 'Группа добавлена.';
                  $scope.page.group = {};                        
              }

              $scope.page.alert = {title: 'Готово!', msg: msg, cssClass: 'alert alert-success', visible: true, closeTimeout: 1500};
              $scope.pageForm.$setPristine();
          },
          function(data, status, headers, config){
              $scope.page.alert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });
   };

   $scope.page.init(); 
});

'use strict';
//ddcdddd

/*===========================================================================================
Студент. Изменение/Добавление
===========================================================================================*/

controllersModule.controller('StudentCtrl', function($scope, $routeParams, StudentSrvc, FacultySrvc){
   $scope.page = {student:{}};

   // Инициализация данных
   $scope.page.init = function(){
      if ($routeParams.id){
          $scope.page.formCaption = "Редактирование студента";
          $scope.page.formBtnSubmitName = "Сохранить";
          $scope.page.loadStudent($routeParams.id);        
      }
      else{
          $scope.page.formCaption = "Добавление студента";
          $scope.page.formBtnSubmitName = "Добавить";

          // Добавление студента в определённую группу
          if ($routeParams.grId && $routeParams.fcId){
              $scope.page.student = {group: {id: parseInt($routeParams.grId), faculty: {id: parseInt($routeParams.fcId)}}};            
          	  $scope.page.loadFacultyGroups($routeParams.fcId);
          }
      }

      // Загрузить факультеты
      $scope.page.loadFaculties();
   };

   // Загрузить студента по ИД
   $scope.page.loadStudent = function(id){
      StudentSrvc.get(id).then(
          function(data){
              $scope.page.student = data;
              // Загрузить группы факультета по ид факультета, на котором учится студент
      		    $scope.page.loadFacultyGroups(data.group.faculty.id);
          },
          function(data, status, headers, config){
              $scope.page.gralert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });       
   };

  // Загрузить все факультеты
  $scope.page.loadFaculties = function(){
      FacultySrvc.getAll(false).then(
          function(data){
              $scope.page.faculties = data.children;
          },
          function(data, status, headers, config){
              $scope.page.gralert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });    
   };

   // Загрузить все группы факультета
  $scope.page.loadFacultyGroups = function(id){
      FacultySrvc.getGroups(id, false).then(
          function(data){
              $scope.page.groups = data.children;
          },
          function(data, status, headers, config){
              $scope.page.alert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });      
   };

   // Сохранить/создать (отправка формы). Передаем объект на сервер
   $scope.page.submit = function(){
      StudentSrvc.save($scope.page.student).then(
          function(data){
              var msg = 'Изменения сохранены.';

              if (!$routeParams.id){
                  msg = 'Студент добавлен.';
                  $scope.page.student = {};                        
              }

              $scope.page.alert = {title: 'Готово!', msg: msg, cssClass: 'alert alert-success', visible: true, closeTimeout: 1500};
              // Сделать форму "чистой" (блокируется кнопка)
              $scope.pageForm.$setPristine();
          },
          function(data, status, headers, config){
              $scope.page.alert = {title: 'Внимание!', msg: data, cssClass: 'alert alert-error', visible: true};  
          });
   };

   $scope.page.init(); 
});

function Ctrl2($scope) {
  $scope.format = 'M/d/yy h:mm:ss a';
}

angular.module('time', [])
  // Регистрируем фабричный метод для директивы 'myCurrentTime'.
  // Внедряем $timeout и сервис dateFilter пока фабричный метод DI.
  .directive('myCurrentTime', function($timeout, dateFilter) {
    // возвращаем функцию линковки директивы. (компилировать функцию не нужно)
    return function(scope, element, attrs) {
      var format,  // формат даты
          timeoutId; // timeoutId, так что мы можем останавливать обновление времени

      // используя обновление UI
      function updateTime() {
        element.text(dateFilter(new Date(), format));
      }

      // проверка выражения и обновление UI при изменении.
      scope.$watch(attrs.myCurrentTime, function(value) {
        format = value;
        updateTime();
      });

      // расписание обновлений за секунду
      function updateLater() {
        // сохраняем timeoutId для отмены
        timeoutId = $timeout(function() {
          updateTime(); // обновляем DOM
          updateLater(); // расписание других обновлений
        }, 1000);
      }

      // прослушиваем разрушающее (removal) DOM событие, и отменяем следующее обновление UI
      // для предотвращения обновления времени после того как DOM-элемент был удален.
      element.bind('$destroy', function() {
        $timeout.cancel(timeoutId);
      });

      updateLater(); // убиваем процесс обновления UI.
    }
  });

