Method.Implementation:

set st = $$$OK	
try{
	set proxy = {}
	set proxy.children = []		
	&sql(DECLARE CoCur CURSOR FOR 
	 	 SELECT ID
	 	 FROM fishnet.Company)	

	&sql(OPEN CoCur)
	for  
	{	
		&sql(FETCH CoCur INTO :id) 
		quit:(SQLCODE '= 0)

		do proxy.children.%Push(##class(fishnet.Company).%OpenId(id).CompanyToJSON())
	}		
	&sql(CLOSE CoCur)		
	w proxy.%ToJSON()
}
catch (ex){
	set st = ex.AsStatus()
}	
quit st

	set st = $$$OK

